%% attribute chargerID(1) with type OBJECT IDENTIFIER
Enc5@element = element(2, Val),
Enc6@bin = encode_oid(Enc5@element),
Enc6@len = byte_size(Enc6@bin),
if Enc6@len < 128 ->
[Enc6@len|Enc6@bin];
Enc6@len < 16384 ->
[<<2:2,Enc6@len:14>>|Enc6@bin];
true ->
encode_fragmented(Enc6@bin, 8)
end
end,
begin
%% attribute chargingDescription(2) with type IA5String
Enc7@element = element(3, Val),
if Enc7@element =:= asn1_NOVALUE ->
[];
true ->
begin
Enc8@len = length(Enc7@element),
Enc8@bin = encode_chars(Enc7@element, 7),
Enc8@len@sub = Enc8@len - 1,
if 0 =< Enc8@len@sub, Enc8@len@sub < 63 ->
[<<Enc8@len@sub:6>>|Enc8@bin]
end
end
end
end,
begin
%% attribute configID(3) with type INTEGER
Enc10@element = element(4, Val),
if Enc10@element =:= asn1_NOVALUE ->
[];
0 =< Enc10@element, Enc10@element < 65 ->
<<0:1,Enc10@element:7>>;
true ->
[<<1:1>>|encode_unconstrained_number(Enc10@element)]
end
end,
begin
%% attribute tripInfo(4) with type TripInfo
Enc13@element = element(5, Val),
if Enc13@element =:= asn1_NOVALUE ->
[];
true ->
enc_TripInfo(Enc13@element)
end
end|begin
%% attribute totalPaidToCharger(5) with type PaymentFee
Enc15@element = element(6, Val),
'EfcDataDictionary':enc_PaymentFee(Enc15@element)
end].


